mapper.insert:
insert into attraction_info (content_id, content_type_id, title, addr1,tel,first_image,sido_code,gugun_code,latitude,longitude)
    values (#{contentID}, #{contentTypeID}, #{title},#{address},#{tel},#{firstImage},#{sidoCode},#{gugunCode},#{latitude},#{longitude}) ;
insert into attraction_description (content_id, userName, title, content)
    values (#{userId}, #{userName}, #{title},#{content});
