mapper.select:
select token
from user
where user_id = #{id}

mapper.update:
update user
set token = #{token}
where user_id = #{user_id}

update user
set token = #{token, jdbcType=VARCHAR}
where user_id = #{id}

mapper.select:
select user_name, user_id, user_email, joindate
from user
where user_id = #{userid}
